(1, 1, 'Store', 'Thiết kế trang thương mại điện tử hiện đại', 'uploads/templates/1741964565_- Find & Share on GIPHY.gif', 100.00, 'Approved', '2025-03-12 23:55:00', NULL, '<?php session_start(); ?>\r\n<!DOCTYPE html>\r\n<html lang=\"vi\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>Webs Community - Tạo Website Bằng Mô Tả</title>\r\n    <link href=\"https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700;800&display=swap\" rel=\"stylesheet\">\r\n    <link rel=\"stylesheet\" href=\"https://cdn.jsdelivr.net/npm/bootstrap-icons@1.7.2/font/bootstrap-icons.css\">\r\n    <link rel=\"stylesheet\" href=\"style.css\">\r\n</head>\r\n<body>\r\n    <header>\r\n        <div class=\"top-header\">\r\n            <div class=\"container\">\r\n                <div class=\"header-left\">\r\n                    <a href=\"/\" class=\"logo\">Webs</a>\r\n                    <div class=\"search-box\">\r\n                        <i class=\"bi bi-search\"></i>\r\n                        <input type=\"text\" placeholder=\"Tìm kiếm mẫu website...\">\r\n                    </div>\r\n                </div>\r\n                \r\n                <nav class=\"main-nav\">\r\n                    <a href=\"#\" class=\"nav-link\">Mẫu Website</a>\r\n                    <a href=\"#\" class=\"nav-link\">Cộng Đồng</a>\r\n                    <a href=\"#\" class=\"nav-link\">Tạo Mới</a>\r\n                    <a href=\"#\" class=\"nav-link\">Bảng Giá</a>\r\n                </nav>\r\n\r\n                <div class=\"auth-buttons\">\r\n                    <?php if(isset($_SESSION[\'user_id\'])): ?>\r\n                        <div class=\"user-menu\">\r\n                            <div class=\"user-info\">\r\n                                <img src=\"<?php echo htmlspecialchars($_SESSION[\'avatar\'] ?? \'images/default-avatar.png\'); ?>\" alt=\"Avatar\" class=\"avatar\">\r\n                                <span class=\"username\"><?php echo htmlspecialchars($_SESSION[\'username\']); ?></span>\r\n                                <i class=\"bi bi-chevron-down\"></i>\r\n                            </div>\r\n                            <div class=\"dropdown-menu\">\r\n                                <a href=\"profile.php\"><i class=\"bi bi-person\"></i> Trang cá nhân</a>\r\n                                <a href=\"my-designs.php\"><i class=\"bi bi-grid\"></i> Thiết kế của tôi</a>\r\n                                <a href=\"settings.php\"><i class=\"bi bi-gear\"></i> Cài đặt</a>\r\n                                <div class=\"dropdown-divider\"></div>\r\n                                <a href=\"logout.php\" class=\"logout\"><i class=\"bi bi-box-arrow-right\"></i> Đăng xuất</a>\r\n                            </div>\r\n                        </div>\r\n                    <?php else: ?>\r\n                        <a href=\"login.php\" class=\"btn-login\">Đăng nhập</a>\r\n                        <a href=\"register.php\" class=\"btn-register\">Đăng ký</a>\r\n                    <?php endif; ?>\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </header>\r\n\r\n    <main>\r\n        <section class=\"hero\">\r\n            <div class=\"container\">\r\n                <h1>Tạo Website Chuyên Nghiệp<br>Bằng Mô Tả</h1>\r\n                <p>Biến ý tưởng của bạn thành website thực tế chỉ bằng vài câu mô tả</p>\r\n                <a href=\"template-user.php\" class=\"btn-primary\" style=\"text-decoration: none;\">Bắt Đầu Ngay</a>\r\n            </div>\r\n        </section>\r\n\r\n        <section class=\"features\">\r\n            <div class=\"container\">\r\n                <div class=\"feature-grid\">\r\n                    <div class=\"feature-card\">\r\n                        <i class=\"bi bi-robot\"></i>\r\n                        <h3>Tạo Website Bằng AI</h3>\r\n                        <p>Chỉ cần mô tả ý tưởng, AI sẽ giúp bạn tạo website</p>\r\n                    </div>\r\n                    <div class=\"feature-card\">\r\n                        <i class=\"bi bi-collection\"></i>\r\n                        <h3>Thư Viện Mẫu</h3>\r\n                        <p>Hàng nghìn mẫu website do cộng đồng đóng góp</p>\r\n                    </div>\r\n                    <div class=\"feature-card\">\r\n                        <i class=\"bi bi-pencil-square\"></i>\r\n                        <h3>Tùy Chỉnh Dễ Dàng</h3>\r\n                        <p>Chỉnh sửa trực quan với công cụ kéo thả</p>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n        </section>\r\n\r\n        <section class=\"templates\">\r\n            <div class=\"container\">\r\n                <div class=\"section-header\">\r\n                    <h2>Khám Phá Tác Phẩm Cộng Đồng</h2>\r\n                    <div class=\"template-filters\">\r\n                        <select class=\"filter-select\">\r\n                            <option value=\"newest\">Mới nhất</option>\r\n                            <option value=\"popular\">Phổ biến nhất</option>\r\n                            <option value=\"trending\">Xu hướng</option>\r\n                        </select>\r\n                    </div>\r\n                </div>\r\n                \r\n                <div class=\"template-grid\">\r\n                    <?php\r\n                    require_once \'connect_db.php\';\r\n                    \r\n                    try {\r\n                        $sql = \"SELECT cd.*, u.Username, t.TemplateName \r\n                                FROM community_designs cd\r\n                                INNER JOIN users u ON cd.UserID = u.UserID \r\n                                INNER JOIN templates t ON cd.TemplateID = t.TemplateID\r\n                                ORDER BY cd.CreatedDate DESC\";\r\n                        \r\n                        $stmt = $conn->prepare($sql);\r\n                        $stmt->execute();\r\n                        \r\n                        while($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\r\n                            ?>\r\n                            <div class=\"template-card\">\r\n                                <img src=\"<?php echo htmlspecialchars($row[\'PreviewImage\']); ?>\" \r\n                                     alt=\"<?php echo htmlspecialchars($row[\'DesignName\']); ?>\">\r\n                                <div class=\"template-info\">\r\n                                    <h3><?php echo htmlspecialchars($row[\'DesignName\']); ?></h3>\r\n                                    <p><?php echo htmlspecialchars($row[\'Description\']); ?></p>\r\n                                    <div class=\"template-meta\">\r\n                                        <span class=\"author\">\r\n                                            <i class=\"bi bi-person\"></i>\r\n                                            <?php echo htmlspecialchars($row[\'Username\']); ?>\r\n                                        </span>\r\n                                        <div class=\"meta-stats\">\r\n                                            <span>\r\n                                                <i class=\"bi bi-eye\"></i>\r\n                                                <?php echo number_format($row[\'Views\']); ?>\r\n                                            </span>\r\n                                            <span>\r\n                                                <i class=\"bi bi-heart\"></i>\r\n                                                <?php echo number_format($row[\'Likes\']); ?>\r\n                                            </span>\r\n                                        </div>\r\n                                    </div>\r\n                                </div>\r\n                                <div class=\"template-overlay\">\r\n                                    <div class=\"overlay-buttons\">\r\n                                        <a href=\"template-