n\r\n/*** Spinner ***/\r\n#spinner {\r\n    opacity: 0;\r\n    visibility: hidden;\r\n    transition: opacity .5s ease-out, visibility 0s linear .5s;\r\n    z-index: 99999;\r\n}\r\n\r\n#spinner.show {\r\n    transition: opacity .5s ease-out, visibility 0s linear 0s;\r\n    visibility: visible;\r\n    opacity: 1;\r\n}\r\n\r\n\r\n/*** Button ***/\r\n.btn {\r\n    font-weight: 500;\r\n    transition: .5s;\r\n}\r\n\r\n.btn.btn-primary,\r\n.btn.btn-outline-primary:hover {\r\n    color: #FFFFFF;\r\n}\r\n\r\n.btn-square {\r\n    width: 38px;\r\n    height: 38px;\r\n}\r\n\r\n.btn-sm-square {\r\n    width: 32px;\r\n    height: 32px;\r\n}\r\n\r\n.btn-lg-square {\r\n    width: 48px;\r\n    height: 48px;\r\n}\r\n\r\n.btn-square,\r\n.btn-sm-square,\r\n.btn-lg-square {\r\n    padding: 0;\r\n    display: flex;\r\n    align-items: center;\r\n    justify-content: center;\r\n    font-weight: normal;\r\n}\r\n\r\n/*** Navbar ***/\r\n.navbar.sticky-top {\r\n    top: -100px;\r\n    transition: .5s;\r\n}\r\n\r\n.navbar .navbar-brand,\r\n.navbar a.btn {\r\n    height: 75px;\r\n}\r\n\r\n.navbar .navbar-nav .nav-link {\r\n    margin-right: 30px;\r\n    padding: 25px 0;\r\n    color: var(--dark);\r\n    font-size: 15px;\r\n    font-weight: 500;\r\n    text-transform: uppercase;\r\n    outline: none;\r\n}\r\n\r\n.navbar .navbar-nav .nav-link:hover,\r\n.navbar .navbar-nav .nav-link.active {\r\n    color: var(--primary);\r\n}\r\n\r\n.navbar .dropdown-toggle::after {\r\n    border: none;\r\n    content: \"\\f107\";\r\n    font-family: \"Font Awesome 5 Free\";\r\n    font-weight: 900;\r\n    vertical-align: middle;\r\n    margin-left: 8px;\r\n}\r\n\r\n@media (max-width: 991.98px) {\r\n    .navbar .navbar-nav .nav-link  {\r\n        margin-right: 0;\r\n        padding: 10px 0;\r\n    }\r\n\r\n    .navbar .navbar-nav {\r\n        border-top: 1px solid #EEEEEE;\r\n    }\r\n}\r\n\r\n@media (min-width: 992px) {\r\n    .navbar .nav-item .dropdown-menu {\r\n        display: block;\r\n        border: none;\r\n        margin-top: 0;\r\n        top: 150%;\r\n        opacity: 0;\r\n        visibility: hidden;\r\n        transition: .5s;\r\n    }\r\n\r\n    .navbar .nav-item:hover .dropdown-menu {\r\n        top: 100%;\r\n        visibility: visible;\r\n        transition: .5s;\r\n        opacity: 1;\r\n    }\r\n}\r\n\r\n\r\n/*** Header ***/\r\n.owl-carousel-inner {\r\n    position: absolute;\r\n    width: 100%;\r\n    height: 100%;\r\n    top: 0;\r\n    left: 0;\r\n    display: flex;\r\n    align-items: center;\r\n    background: rgba(0, 0, 0, .1);\r\n}\r\n\r\n@media (max-width: 768px) {\r\n    .header-carousel .owl-carousel-item {\r\n        position: relative;\r\n        min-height: 500px;\r\n    }\r\n\r\n    .header-carousel .owl-carousel-item img {\r\n        position: absolute;\r\n        width: 100%;\r\n        height: 100%;\r\n        object-fit: cover;\r\n    }\r\n\r\n    .header-carousel .owl-carousel-item p {\r\n        font-size: 16px !important;\r\n    }\r\n}\r\n\r\n.header-carousel .owl-dots {\r\n    position: absolute;\r\n    width: 60px;\r\n    height: 100%;\r\n    top: 0;\r\n    right: 30px;\r\n    display: flex;\r\n    flex-direction: column;\r\n    align-items: center;\r\n    justify-content: center;\r\n}\r\n\r\n.header-carousel .owl-dots .owl-dot {\r\n    position: relative;\r\n    width: 45px;\r\n    height: 45px;\r\n    margin: 5px 0;\r\n    background: #FFFFFF;\r\n    box-shadow: 0 0 30px rgba(255, 255, 255, .9);\r\n    border-radius: 45px;\r\n    transition: .5s;\r\n}\r\n\r\n.header-carousel .owl-dots .owl-dot.active {\r\n    width: 60px;\r\n    height: 60px;\r\n}\r\n\r\n.header-carousel .owl-dots .owl-dot img {\r\n    position: absolute;\r\n    width: 100%;\r\n    height: 100%;\r\n    object-fit: cover;\r\n    padding: 2px;\r\n    border-radius: 45px;\r\n    transition: .5s;\r\n}\r\n\r\n.page-header {\r\n    background: linear-gradient(rgba(0, 0, 0, .1), rgba(0, 0, 0, .1)), url(../img/carousel-1.jpg) center center no-repeat;\r\n    background-size: cover;\r\n}\r\n\r\n.breadcrumb-item + .breadcrumb-item::before {\r\n    color: var(--light);\r\n}\r\n\r\n\r\n/*** About ***/\r\n@media (min-width: 992px) {\r\n    .container.about {\r\n        max-width: 100% !important;\r\n    }\r\n\r\n    .about-text  {\r\n        padding-right: calc(((100% - 960px) / 2) + .75rem);\r\n    }\r\n}\r\n\r\n@media (min-width: 1200px) {\r\n    .about-text  {\r\n        padding-right: calc(((100% - 1140px) / 2) + .75rem);\r\n    }\r\n}\r\n\r\n@media (min-width: 1400px) {\r\n    .about-text  {\r\n        padding-right: calc(((100% - 1320px) / 2) + .75rem);\r\n    }\r\n}\r\n\r\n\r\n/*** Service ***/\r\n.service-item {\r\n    box-shadow: 0 0 45px rgba(0, 0, 0, .08);\r\n}\r\n\r\n.service-icon {\r\n    position: relative;\r\n    margin: -50px 0 25px 0;\r\n    width: 100px;\r\n    height: 100px;\r\n    display: flex;\r\n    align-items: center;\r\n    justify-content: center;\r\n    color: var(--primary);\r\n    background: #FFFFFF;\r\n    border-radius: 100px;\r\n    box-shadow: 0 0 45px rgba(0, 0, 0, .08);\r\n    transition: .5s;\r\n}\r\n\r\n.service-item:hover .service-icon {\r\n    color: #FFFFFF;\r\n    background: var(--primary);\r\n}\r\n\r\n\r\n/*** Feature ***/\r\n@media (min-width: 992px) {\r\n    .container.feature {\r\n        max-width: 100% !important;\r\n    }\r\n\r\n    .feature-text  {\r\n        padding-left: calc(((100% - 960px) / 2) + .75rem);\r\n    }\r\n}\r\n\r\n@media (min-width: 1200px) {\r\n    .feature-text  {\r\n        padding-left: calc(((100% - 1140px) / 2) + .75rem);\r\n    }\r\n}\r\n\r\n@media (min-width: 1400px) {\r\n    .feature-text  {\r\n        padding-left: calc(((100% - 1320px) / 2) + .75rem);\r\n    }\r\n}\r\n\r\n\r\n/*** Project Portfolio ***/\r\n#portfolio-flters li {\r\n    display: inline-block;\r\n    font-weight: 500;\r\n    color: var(--dark);\r\n    cursor: pointer;\r\n    transition: .5s;\r\n    border-bottom: 2px solid transparent;\r\n}\r\n\r\n#portfolio-flters li:hover,\r\n#portfolio-flters li.active {\r\n    color: var(--primary);\r\n    border-color: var(--primary);\r\n}\r\n\r\n.portfolio-img {\r\n    position: relative;\r\n}\r\n\r\n.portfolio-img::before,\r\n.portfolio-img::after {\r\n    position: absolute;\r\n    content: \"\";\r\n    width: 0;\r\n    height: 100%;\r\n    top: 0;\r\n    background: var(--dark);\r\n    transition: .5s;\r\n}\r\n\r\n.portfolio-img::before {\r\n    left: 50%;\r\n}\r\n\r\n.portfolio-img::after {\r\n    right: 50%;\r\n}\r\n\r\n.portfolio-item:hover .portfolio-img::before {\r\n    width: 51%;\r\n    left: 0;\r\n}\r\n\r\n.portfolio-item:hover .portfolio-img::after {\r\n    width: 51%;\r\n    right: 0;\r\n}\r\n\r\n.portfolio-btn {\r\n    position: absolute;\r\n    top: 0;\r\n    left: 0;\r\n    width: 100%;\r\n    height: 100%;\r\n    display: flex;\r\n    align-items: center;\r\n    justify-content: center;\r\n    opacity: 0;\r\n    z-index: 1;\r\n    transition: .5s;\r\n}\r\n\r\n.portfolio-item:hover .portfolio-btn {\r\n    opacity: 1;\r\n    transition-delay: .3s;\r\n}\r\n\r\n\r\n/*** Quote ***/\r\n@media (min-width: 992px) {\r\n    .container.quote {\r\n        max-width: 100% !important;\r\n    }\r\n\r\n    .quote-text  {\r\n        padding-right: calc(((100% - 960px) / 2) + .75rem);\r\n    }\r\n}\r\n\r\n@media (min-width: 1200px) {\r\n    .quote-text  {\r\n        padding-right: calc(((100% - 1140px) / 2) + .75rem);\r\n    }\r\n}\r\n\r\n@media (min-width: 1400px) {\r\n    .quote-text  {\r\n        padding-right: calc(((100% - 1320px) / 2) + .75rem);\r\n    }\r\n}\r\n\r\n\r\n/*** Team ***/\r\n.team-item {\r\n    box-shadow: 0 0 45px rgba(0, 0, 0, .08);\r\n}\r\n\r\n.team-item img {\r\n    border-radius: 8px 60px 0 0;\r\n}\r\n\r\n.team-item .team-social {\r\n    display: flex;\r\n    flex-direction: column;\r\n    align-items: center;\r\n    justify-content: flex-end;\r\n    background: #FFFFFF;\r\n    transition: .5s;\r\n}\r\n\r\n\r\n/*** Testimonial ***/\r\n.testimonial-carousel::before {\r\n    position: absolute;\r\n    content: \"\";\r\n    top: 0;\r\n    left: 0;\r\n    height: 100%;\r\n    width: 0;\r\n    background: linear-gradient(to right, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 0) 100%);\r\n    z-index: 1;\r\n}\r\n\r\n.testimonial-carousel::after {\r\n    position: absolute;\r\n    content: \"\";\r\n    top: 0;\r\n    right: 0;\r\n    height: 100%;\r\n    width: 0;\r\n    background: linear-gradient(to left, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 0) 100%);\r\n    z-index: 1;\r\n}\r\n\r\n@media (min-width: 768px) {\r\n    .testimonial-carousel::before,\r\n    .testimonial-carousel::after {\r\n        width: 200px;\r\n    }\r\n}\r\n\r\n@media (min-width: 992px) {\r\n    .testimonial-carousel::before,\r\n    .testimonial-carousel::after {\r\n        width: 300px;\r\n    }\r\n}\r\n\r\n.testimonial-carousel .owl-nav {\r\n    position: absolute;\r\n    width: 350px;\r\n    top: 20px;\r\n    left: 50%;\r\n    transform: translateX(-50%);\r\n    display: flex;\r\n    justify-content: space-between;\r\n    opacity: 0;\r\n    transition: .5s;\r\n    z-index: 1;\r\n}\r\n\r\n.testimonial-carousel:hover .owl-nav {\r\n    width: 300px;\r\n    opacity: 1;\r\n}\r\n\r\n.testimonial-carousel .owl-nav .owl-prev,\r\n.testimonial-carousel .owl-nav .owl-next {\r\n    position: relative;\r\n    color: var(--primary);\r\n    font-size: 45px;\r\n    transition: .5s;\r\n}\r\n\r\n.testimonial-carousel .owl-nav .owl-prev:hover,\r\n.testimonial-carousel .owl-nav .owl-next:hover {\r\n    color: var(--dark);\r\n}\r\n\r\n.testimonial-carousel .testimonial-img img {\r\n    width: 100px;\r\n    height: 100px;\r\n}\r\n\r\n.testimonial-carousel .testimonial-img .btn-square {\r\n    position: absolute;\r\n    bottom: -19px;\r\n    left: 50%;\r\n    transform: translateX(-50%);\r\n}\r\n\r\n.testimonial-carousel .owl-item .testimonial-text {\r\n    margin-bottom: 30px;\r\n    box-shadow: 0 0 45px rgba(0, 0, 0, .08);\r\n    transform: scale(.8);\r\n    transition: .5s;\r\n}\r\n\r\n.testimonial-carousel .owl-item.center .testimonial-text {\r\n    transform: scale(1);\r\n}\r\n\r\n\r\n/*** Contact ***/\r\n@media (min-width: 992px) {\r\n    .container.contact {\r\n        max-width: 100% !important;\r\n    }\r\n\r\n    .contact-text  {\r\n        padding-left: calc(((100% - 960px) / 2) + .75rem);\r\n    }\r\n}\r\n\r\n@media (min-width: 1200px) {\r\n    .contact-text  {\r\n        padding-left: calc(((100% - 1140px) / 2) + .75rem);\r\n    }\r\n}\r\n\r\n@media (min-width: 1400px) {\r\n    .contact-text  {\r\n        padding-left: calc(((100% - 1320px) / 2) + .75rem);\r\n    }\r\n}\r\n\r\n\r\n/*** Footer ***/\r\n.footer .btn.btn-social {\r\n    margin-right: 5px;\r\n    color: #9B9B9B;\r\n    border: 1px solid #9B9B9B;\r\n    border-radius: 38px;\r\n    transition: .3s;\r\n}\r\n\r\n.footer .btn.btn-social:hover {\r\n    color: var(--primary);\r\n    border-color: var(--light);\r\n}\r\n\r\n.footer .btn.btn-link {\r\n    display: block;\r\n    margin-bottom: 5px;\r\n    padding: 0;\r\n    text-align: left;\r\n    color: #9B9B9B;\r\n    font-weight: normal;\r\n    text-transform: capitalize;\r\n    transition: .3s;\r\n}\r\n\r\n.footer .btn.btn-link::before {\r\n    position: relative;\r\n    content: \"\\f105\";\r\n    font-family: \"Font Awesome 5 Free\";\r\n    font-weight: 900;\r\n    margin-right: 10px;\r\n}\r\n\r\n.footer .btn.btn-link:hover {\r\n    color: #FFFFFF;\r\n    letter-spacing: 1px;\r\n    box-shadow: none;\r\n}\r\n\r\n.footer .copyright {\r\n    padding: 25px 0;\r\n    border-top: 1px solid rgba(256, 256, 256, .1);\r\n}\r\n\r\n.footer .copyright a {\r\n    color: var(--light);\r\n}\r\n\r\n.footer .copyright a:hover {\r\n    color: var(--primary);\r\n}', '(function ($) {\r\n    \"use strict\";\r\n\r\n    // Spinner\r\n    var spinner = function () {\r\n        setTimeout(function () {\r\n            if ($(\'#spinner\').length > 0) {\r\n                $(\'#spinner\').removeClass(\'show\');\r\n            }\r\n        }, 1);\r\n    };\r\n    spinner();\r\n    \r\n    \r\n    // Initiate the wowjs\r\n    new WOW().init();\r\n\r\n\r\n    // Sticky Navbar\r\n    $(window).scroll(function () {\r\n        if ($(this).scrollTop() > 300) {\r\n            $(\'.sticky-top\').addClass(\'shadow-sm\').css(\'top\', \'0px\');\r\n        } else {\r\n            $(\'.sticky-top\').removeClass(\'shadow-sm\').css(\'top\', \'-100px\');\r\n        }\r\n    });\r\n    \r\n    \r\n    // Back to top button\r\n    $(window).scroll(function () {\r\n        if ($(this).scrollTop() > 300) {\r\n            $(\'.back-to-top\').fadeIn(\'slow\');\r\n        } else {\r\n            $(\'.back-to-top\').fadeOut(\'slow\');\r\n        }\r\n    });\r\n    $(\'.back-to-top\').click(function () {\r\n        $(\'html, body\').animate({scrollTop: 0}, 1500, \'easeInOutExpo\');\r\n        return false;\r\n    });\r\n\r\n\r\n    // Facts counter\r\n    $(\'[data-toggle=\"counter-up\"]\').counterUp({\r\n        delay: 10,\r\n        time: 2000\r\n    });\r\n\r\n\r\n    // Header carousel\r\n    $(\".header-carousel\").owlCarousel({\r\n        autoplay: true,\r\n        smartSpeed: 1500,\r\n        loop: true,\r\n        nav: false,\r\n        dots: true,\r\n        items: 1,\r\n        dotsData: true,\r\n    });\r\n\r\n\r\n    // Testimonials carousel\r\n    $(\".testimonial-carousel\").owlCarousel({\r\n        autoplay: true,\r\n        smartSpeed: 1000,\r\n        center: true,\r\n        dots: false,\r\n        loop: true,\r\n        nav : true,\r\n        navText : [\r\n            \'<i class=\"bi bi-arrow-left\"></i>\',\r\n            \'<i class=\"bi bi-arrow-right\"></i>\'\r\n        ],\r\n        responsive: {\r\n            0:{\r\n                items:1\r\n            },\r\n            768:{\r\n                items:2\r\n            }\r\n        }\r\n    });\r\n\r\n\r\n    // Portfolio isotope and filter\r\n    var portfolioIsotope = $(\'.portfolio-container\').isotope({\r\n        itemSelector: \'.portfolio-item\',\r\n        layoutMode: \'fitRows\'\r\n    });\r\n    $(\'#portfolio-flters li\').on(\'click\', function () {\r\n        $(\"#portfolio-flters li\").removeClass(\'active\');\r\n        $(this).addClass(\'active\');\r\n\r\n        portfolioIsotope.isotope({filter: $(this).data(\'filter\')});\r\n    });\r\n    \r\n})(jQuery);\r\n\r\n', 'business', 2, NULL, NULL, 1, 1, NULL, NULL, NULL);
INSERT INTO `templates` (`TemplateID`, `UserID`, `TemplateName`, `Description`, `PreviewImage`, `Price`, `Status`, `CreatedDate`, `ApprovedDate`, `HTMLContent`, `CSSContent`, `JSContent`, `Category`, `Views`, `Styles`, `LastModified`, `AutoSaved`, `Likes`, `FolderStructure`, `Dependencies`, `Settings`) VALUES
(16, 7, 'My Home', 'Website bán nhà giá rẻ', 'uploads/templates/1742127003_myhome.PNG', 90000.00, 'Approved', '2025-03-16 19:10:03', '2025-03-16 19:10:06', '<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n  <meta charset=\"UTF-8\">\n  <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n  <title>Realestate - Choose your dream home</title>\n  <!--======== custom css link =========-->\n  <link rel=\"stylesheet\" href=\"css/style.css\">\n  <!--========= google font link ========-->\n  <link rel=\"preconnect\" href=\"https://fonts.googleapis.com\">\n  <link rel=\"preconnect\" href=\"https://fonts.gstatic.com\" crossorigin>\n  <link href=\"https://fonts.googleapis.com/css2?family=League+Spartan:wght@400;500;600;700&display=swap\" rel=\"stylesheet\">\n</head>\n<body>\n    \n    \n   <header class=\"header\" data-header>\n        <div class=\"container\">\n            <a href=\"\" class=\"logo\">\n                <ion-icon name=\"home-outline\"></ion-icon> Myhome\n            </a>\n\n            <nav class=\"navbar container\" data-navbar>\n                <ul class=\"navbar-list\">\n                    <li>\n                        <a href=\"#\" class=\"navbar-link\" data-nav-link>Home</a>\n                    </li>\n                    <li>\n                        <a href=\"#\" class=\"navbar-link\" data-nav-link>About Us</a>\n                    </li>\n                    <li>\n                        <a href=\"#\" class=\"navbar-link\" data-nav-link>Services</a>\n                    </li>\n                    <li>\n                        <a href=\"#\" class=\"navbar-link\" data-nav-link>Contact</a>\n                    </li>\n                </ul>\n            </nav>\n\n            <a href=\"#\" class=\"btn btn-secondary\">Signup</a>\n            <button class=\"nav-toggle-btn\" aria-label=\"Toggle menu\" data-nav-toggler>\n                <ion-icon name=\"menu-outline\" aria-hidden=\"true\" class=\"menu-icon\"></ion-icon>\n                <ion-icon name=\"close-outline\" aria-hidden=\"trur\" class=\"close-icon\"></ion-icon>\n\n            </button>\n        </div>\n   </header>\n\n   <!--======== main =========-->\n   <main>\n        <article class=\"article\">\n            <section class=\"section hero\" aria-label=\"hero\">\n                <div class=\"container\">\n                    <div class=\"hero-bg\">\n                        <div class=\"hero-content\">\n                            <h1 class=\"h1 hero-title\">\n                                We will help you find your <span class=\"span\">Dream</span> home\n                            </h1>\n\n                            <p class=\"hero-text\">\n                                Buy your dream home today without any agent or commissions\n                            </p>\n                        </div>\n                    </div>\n\n                    <div class=\"hero-form-wrapper\">\n                        <div class=\"form-tab\">\n                            <button class=\"tab-btn active\" data-tab-btn>Buy</button>\n                            <button class=\"tab-btn \" data-tab-btn>Sell</button>\n                            <button class=\"tab-btn \" data-tab-btn>Rent</button>\n                        </div>\n\n                        <form action=\"\" class=\"hero-form\">\n                            <div class=\"input-wrapper\">\n                                <label for=\"search\" class=\"input-label\">Search: *</label>\n                                <input type=\"search\" name=\"search\" id=\"search\" placeholder=\"Search your home\" required class=\"input-field\">\n                                <ion-icon name=\"search-outline\"></ion-icon>\n                            </div>\n\n                            <div class=\"input-wrapper\">\n                                <label for=\"category\" class=\"input-label\">Select Categories</label>\n                                <select name=\"category\" id=\"category\" class=\"dropdown-list\">\n                                    <option value=\"house\">House</option>\n                                    <option value=\"apartment\">Apartment</option>\n                                    <option value=\"offices\">Offices</option>\n                                    <option value=\"townhome\">Townhome</option>\n                                </select>\n                            </div>\n\n                            <div class=\"input-wrapper\">\n                                <label for=\"min-price\" class=\"input-label\">Min Price :</label>\n                                <select name=\"min-price\" id=\"min-price\" class=\"dropdown-list\">\n                                    <option value=\"min price\">Min Price</option>\n                                    <option value=\"500\">500</option>\n                                    <option value=\"1000\">1000</option>\n                                    <option value=\"2000\">2000</option>\n                                    <option value=\"3000\">3000</option>\n                                    <option value=\"4000\">4000</option>\n                                    <option value=\"5000\">5000</option>\n                                    <option value=\"6000\">6000</option>\n                                </select>\n                            </div>\n\n                            <div class=\"input-wrapper\">\n                                <label for=\"max-price\" class=\"input-label\">Max Price :</label>\n                                <select name=\"max-price\" id=\"max-price\" class=\"dropdown-list\">\n                                    <option value=\"max price\">Max Price</option>\n                                    <option value=\"500\">500</option>\n                                    <option value=\"1000\">1000</option>\n                                    <option value=\"2000\">2000</option>\n                                    <option value=\"3000\">3000</option>\n                                    <option value=\"4000\">4000</option>\n                                    <option value=\"5000\">5000</option>\n                                    <option value=\"6000\">6000</option>\n                                </select>\n                            </div>\n                            <button type=\"submit\" class=\"btn btn-primary\">Search now</button>\n                        </form>\n                    </div>\n                </div>\n            </section>\n\n            <section class=\"section about\" id=\"about\" aria-level=\"about\">\n                <div class=\"container\">\n                    <div class=\"about-banner img-holder\" style=\"--width: 600; --height: 700;\">\n                        <img src=\"images/abt-banner.jpg\" width=\"600px\" height=\"700px\" loading=\"lazy\" alt=\"\" class=\"img-cover\">\n\n                        <button class=\"play-btn\" aria-label=\"play video\">\n                            <ion-icon name=\"play\" aria-hidden=\"true\"></ion-icon>\n                        </button>\n                    </div>\n\n                    <div class=\"about-content\">\n                        <h2 class=\"h2 section-title\">Trusted. Affordable. Transparent</h2>\n                        <p class=\"section-text\">Lorem ipsum dolor sit amet consectetur adipisicing elit. Deserunt commodi ex illo quisquam impedit, corrupti odio placeat dolores eius voluptas? Mollitia quas laborum dolores ipsam dolor. Quo provident at modi!</p>\n                        <a href=\"#\" class=\"btn btn-primary\">Learn More</a>\n                    </div>\n                </div>\n            </section>\n\n            <!--========== SECTION ==========-->\n            <section class=\"section service\" id=\"service\" aria-label=\"service\">\n                <div class=\"container\">\n                    <h2 class=\"section-title\">How It Works</h2>\n                    <p cl