/*======== colors =========*/\n\n  --oxford-blue_60: hsla(224, 34%, 13%, 0.6);\n  --green-pigment: hsl(202, 86%, 51%);\n  --oxford-blue: hsl(224, 34%, 13%);\n  --maximum-red: hsl(0, 72%, 51%);\n  --eerie-black: hsl(0, 0%, 9%);\n  --mint-cream: hsl(146, 41%, 97%);\n  --cadet-gray: hsl(215, 20%, 65%);\n  --light-gray: hsl(216, 12%, 84%);\n  --carcoal_20: hsla(214, 19%, 29%, 0.2);\n  --carcoal_15: hsla(214, 19%, 29%, 0.15);\n  --carcoal_10: hsla(214, 19%, 29%, 0.10);\n  --sea-green: hsl(205, 77%, 41%);\n  --gainsboro: hsl(220, 13%, 91%);\n  --white_70: hsla(0, 0%, 100%, 0.7);\n  --gunmetal: hsl(215, 28%, 17%);\n  --manatee: hsl(218, 11%, 65%);\n  --mango: hsl(43, 96%, 56%);\n  --white: hsl(0, 0%, 100%);\n  --black: hsl(0, 0%, 0%);\n\n  /*======= typography ========*/\n\n  --ff-spartan: \'League Spartan\', sans-serif;\n\n  --fs-1: 3.6rem;\n  --fs-2: 2.4rem;\n  --fs-3: 2rem;\n  --fs-4: 1.8rem;\n  --fs-5: 1.7rem;\n\n  --fw-600: 600;\n  --fw-500: 500;\n\n  /*======== spacing =========*/\n\n  --section-padding: 35px;\n\n  /*======= shadow =======*/\n\n  --shadow-1: 0 1px 2px hsla(0, 0%, 0%, 0.2);\n  --shadow-2: 0 10px 15px -5px var(--carcoal_20);\n  --shadow-3: 0 10px 25px -3px var(--carcoal_20);\n  --shadow-4: 0 0 3px var(--carcoal_15);\n  --shadow-5: 0 20px 25px -5px var(--carcoal_10),\n              0 10px 10px -5px var(--carcoal_10);\n\n  /*======== border radius ========*/\n\n  --radius-16: 16px;\n  --radius-12: 12px;\n  --radius-6: 6px;\n  --radius-4: 4px;\n\n  /*======== transition =========*/\n\n  --transition: 0.25s ease;\n  --cubic-out: cubic-bezier(0.33, 0.85, 0.4, 0.96);\n\n}\n/*========= RESET =========*/\n*,\n*::before,\n*::after {\n  margin: 0;\n  padding: 0;\n  box-sizing: border-box;\n}\n\nli { \n  list-style: none; \n}\n\na {\n  text-decoration: none;\n  color: inherit;\n}\n\na,\nimg,\nspan,\nlabel,\ninput,\nselect,\nbutton,\nion-icon { \n  display: block; \n}\n\nimg { \n  height: auto; \n}\n\ninput,\nselect,\nbutton {\n  background: none;\n  border: none;\n  font: inherit;\n}\n\ninput,\nselect { \n  width: 100%; \n}\n\nbutton { \n  cursor: pointer; \n}\n\nion-icon { \n  pointer-events: none; \n}\n\naddress { \n  font-style: normal; \n}\n\nhtml {\n  font-family: var(--ff-spartan);\n  font-size: 10px;\n  scroll-behavior: smooth;\n}\n\nbody {\n  background-color: var(--white);\n  font-size: var(--fs-5);\n  color: var(--cadet-gray);\n}\n\n/*======== REUSABLE STYLE ==========*/\n\n.container { \n  padding-inline: 15px; \n}\n\n.btn {\n  background-color: var(--green-pigment);\n  color: var(--white);\n  font-weight: var(--fw-500);\n  max-width: max-content;\n  padding: var(--padding, 12px 28px);\n}\n\n.btn:is(:hover, :focus) { \n  background-color: var(--sea-green); \n}\n\n.btn-secondary { \n  border-radius: 50px; \n}\n\n.btn-primary { \n  border-radius: var(--radius-6); \n}\n\n.h1 { \n  font-size: var(--fs-1); \n}\n\n.h2,\n.h3 {\n  color: var(--oxford-blue);\n  line-height: 1.5;\n}\n\n.h2 {\n  font-size: var(--fs-2);\n  font-weight: var(--fw-500);\n}\n\n.h3 {\n  font-size: var(--fs-3);\n  font-weight: var(--fw-500);\n}\n\n.section { padding-block: var(--section-padding); }\n\n.img-holder {\n  aspect-ratio: var(--width) / var(--height);\n  background-color: var(--gainsboro);\n}\n\n.img-cover {\n  width: 100%;\n  height: 100%;\n  object-fit: cover;\n}\n\n.section-title { \n  color: var(--oxford-blue); \n}\n\n.section-text { \n  line-height: 1.6; \n}\n\n/*======== HEADER STYLE =========*/\n.header{\n    background-color: var(--white);\n    position: fixed;\n    top: 0;\n    left: 0;\n    width: 100%;\n    padding-block: 15px;\n    z-index: 4;\n}\n\n.header.active{\n    box-shadow: var(--shadow-1);\n}\n\n.header > .container{\n    display: flex;\n    justify-content: space-between;\n    align-items: center;\n    gap: 10px;\n}\n\n.logo{\n    font-size: 2.2rem;\n    font-weight: var(--fw-600);\n    color: var(--black);\n}\n\n.logo ion-icon{\n    display: inline-block;\n    color: var(--sea-green);\n    --ionicon-stroke-width: 50px;\n}\n\n.nav-toggle-btn{\n    font-size: 35px;\n    color: var(--oxford-blue);\n}\n\n.nav-toggle-btn ion-icon{\n    --ionicon-stroke-width: 30px;\n}\n\n.nav-toggle-btn.active .menu-icon,\n.nav-toggle-btn .close-icon{\n    display: none;\n}\n\n.nav-toggle-btn .menu-icon,\n.nav-toggle-btn.active .close-icon{\n    display: block;\n}\n\n.header .btn{\n    margin-inline-start: auto;\n}\n\n.navbar{\n    position: absolute;\n    top: 100%;\n    left: 50%;\n    transform: translateX(-50%);\n    width: 100%;\n    background-color: var(--white);\n    padding: 0;\n    max-height: 0;\n    overflow: hidden;\n    visibility: visible;\n    transition: .25s var(--cubic-out);\n}\n\n\n.navbar.active{\n    max-height: 260px;\n    visibility: visible;\n    box-shadow: var(--shadow-1);\n    transition-duration: 0.5s;\n}\n\n.navbar-list{\n    margin-block: 10px;\n    opacity: 0;\n    transition: var(--transition);\n}\n\n.navbar.active .navbar-list{\n    opacity: 1;\n}\n\n.navbar-link{\n    color: var(--oxford-blue);\n    font-weight: var(--fw-500);\n    letter-spacing: 1px;\n    padding: 12px 25px;\n}\n\n.navbar-link:is(hover, :focus){\n    color: var(--green-pigment);\n}\n\n/*=========== main ==========*/\n.article{\n    position: relative;\n}\n\n.hero{\n    padding-block-start: 80px;\n}\n\n.hero-bg{\n    background-image: url(\"../images/img1.jpg\");\n    background-repeat: no-repeat;\n    background-size: cover;\n    background-position: center;\n    border-radius: var(--radius-16);\n    overflow: hidden;\n}\n\n.hero-content{\n    background-color: var(--oxford-blue_60);\n    padding-block: 180px;\n    padding-inline: 40px;\n    display: grid;\n    grid-template-columns: 1fr;\n    place-content: center;\n    text-align: center;\n}\n\n.hero-title{\n    max-width: 16ch;\n    margin-inline: auto;\n    color: var(--white);\n    line-height: 1.5;\n    margin-block-end: 20px;\n}\n\n.hero-title .span{\n    display: inline-block;\n    color: var(--green-pigment);\n}\n\n.hero-form-wrapper{\n    background-color: var(--white);\n    margin-block-start: -100px;\n    width: calc(100% - 10px);\n    margin-inline: auto;\n    border-radius: var(--radius-16);\n    box-shadow: var(--shadow-2);\n    overflow: hidden;\n}\n\n.form-tab{\n    padding: 16px;\n    border-block-end: 1px solid var(--gainsboro);\n    display: flex;\n    justify-content: center;\n}\n\n.tab-btn{\n    padding: 12px 24px;\n    color: var(--oxford-blue);\n    font-weight: var(--fw-500);\n    border-radius: var(--radius-6);\n    transition: var(--transition);\n}\n\n.tab-btn :is(:hover, :focus){\n    color: var(--white);\n    background-color: var(--green-pigment);\n}\n\n.hero-form{\n    display: grid;\n    gap: 25px;\n    padding: 25px;\n}\n\n.hero-form .input-wrapper{\n    position: relative;\n}\n\n.hero-form:is(.input-label, .input-field){\n    color: var(--oxford-blue);\n}\n\n.hero-form .input-label{\n    font-weight: var(--fw-500);\n    margin-block-end: 10px;\n}\n\n.hero-form :is(.input-field, .dropdown-list){\n    min-height: 40px;\n    border: 1px solid var(--gainsboro);\n    border-radius: var(--radius-4);\n    outline: none;\n    transition: var(--transition);\n}\n\n.hero-form :is(.input-field, .dropdown-list):focus{\n    border-color: var(--green-pigment);\n}\n\n.hero-form .input-wrapper ion-icon{\n    position: absolute;\n    top: 50%;\n    transform: translateY(20%);\n    left: 15px;\n    color: var(--oxford-blue);\n    --ionicon-stroke-width: 20px\n}\n\n.hero-form .input-field{\n    padding-inline: 45px 15px;\n}\n\n.hero-form .input-field::placeholder{\n    color: var(--manatee);\n}\n\n.hero-form .dropdown-lis{\n    padding-inline: 15px;\n}\n\n.hero-form .btn{\n    max-width: 100%;\n}\n\n/*========== ABOUT ==========*/\n.about .section-title{\n    max-width: 20ch;\n}\n\n.about-banner{\n    position: relative;\n    overflow: hidden;\n    border-radius: var(--radius-16);\n    margin-block-end: 30px;\n}\n\n.about-banner .play-btn{\n    position: absolute;\n    top: 50%;\n    left: 50%;\n    transform: translate(-50%, -50%);\n    background-color: var(--white);\n    color: var(--green-pigment);\n    padding: 30px;\n    font-size: 20px;\n    border-radius: 50px;\n}\n\n.about .section-text{\n    margin-block: 20px 25px;\n}\n\n/*========== SERVICES =========*/\n\n.service :is(.section-title, .section-text){\n    text-align: center;\n}\n\n.service .section-text{\n    margin-block: 15px 16px;\n}\n\n.service-list{\n    display: grid;\n    gap: 30px;\n}\n\n.service-card{\n    text-align: center;\n    border-radius: 10px;\n    transition: .3s;\n    padding: 10px;\n}\n\n.service-card:hover{\n    box-shadow: var(--shadow-5);\n}\n\n.service-card .card-icon{\n    background-color: var(--mint-cream);\n    width: 100px;\n    height: 100px;\n    display: grid;\n    place-items: center;\n    border-radius: 50%;\n    margin-inline: auto;\n}\n\n.service-card .card-icon ion-icon{\n    font-size: 30px;\n    color: var(--green-pigment);\n    --ionicon-stroke-width: 45px;\n}\n\n.service-card .card-title{\n    margin-block: 25px 12px;\n}\n\n.service-card .card-text{\n    line-height: 1.6;\n}\n\n/*========== PROPERTIES =========*/\n\n.property :is(.section-title, .section-text){\n    text-align: center;\n}\n\n.property .section-text{\n    margin-block: 15px 60px;\n}\n\n.property-list{\n    display: grid;\n    gap: 30px;\n}\n\n.property-card{\n    position: relative;\n    border-radius: var(--radius-12);\n    box-shadow: var(--shadow-4);\n    overflow: hidden;\n    transition: var(--transition);\n}\n\n.property-card:is(:hover, :focus-within){\n    box-shadow: var(--shadow-5);\n}\n\n.property-card .card-action-btn{\n    position: absolute;\n    top: 20px;\n    right: 20px;\n    background-color: var(--white);\n    color: var(--maximum-red);\n    padding: 12px;\n    border-radius: 50px;\n}\n\n.property-card .card-content{\n    color: var(--oxford-blue);\n    padding: 25px;\n}\n\n.property-card .card-title{\n    font-size: var(--fs-4);\n    transition: var(--transition);\n}\n\n.property-card .card-title:is(:hover, :focus){\n    color: var(--green-pigment);\n}\n\n.property-card .card-list{\n    display: flex;\n    flex-wrap: wrap;\n    justify-content: flex-start;\n    align-items: center;\n    gap: 10px 15px;\n    padding-block: 30px;\n    border-block: 1px solid var(--gainsbro);\n    margin-block: 30px;\n}\n\n.property-card .card-item{\n    display: flex;\n    align-items: center;\n    gap: 5px;\n}\n\n.property-card .item-icon{\n    color: var(--green-pigment);\n    font-size: 24px;\n}\n\n.property-card .item-icon ion-icon{\n    --ionicon-stroke-width: 40px\n}\n\n.property-card .card-meta{\n    display: flex;\n    flex-wrap: wrap-reverse;\n    justify-content: space-between;\n    align-items: center;\n    gap: 20px;\n}\n\n.property-card .meta-title{\n    color: var(--cadet-gray);\n    margin-block-end: 10px;\n}\n\n.property-card .meta-text{\n    font-size: var(--fs-4);\n    font-weight: var(--fw-500);\n    display: flex;\n    gap: 8px;\n}\n\n.property-card .rating-wrapper{\n    display: flex;\n    gap: 5px;\n    color: var(--mango);\n    font-size: 16px;\n}\n\n/*============ TESTIMONY =========*/\n.wrapper{\n    width: 100%;\n    height: 480px;\n    margin: 7.5rem 0;\n    position: relative;\n}\n\n.wrapper .text{\n    background: #3f9cc1;\n    padding: 50px;\n    width: 40%;\n    height: 600px;\n    position: absolute;\n    top: -13%;\n    right: 10%;\n    padding-top: 10%;\n    color: #fff;\n}\n\n.wrapper .content{\n    display: grid;\n    grid-template-columns: repeat(2, 1fr);\n    grid-row-gap: 30px;\n}\n\n.wrapper h2{\n    font-weight: 400;\n}\n\n.wrapper p{\n    margin: 20px 0 50px 0;\n    line-height: 30px;\n}\n\n.wrapper2{\n    background: url(\'../images/img4.jpg\') fixed;\n    background-repeat: no-repeat;\n    background-size: cover;\n}\n\n.wrapper2 .text{\n    left: 10%;\n}\n\n.wrapper2 h5,\n.wrapper2 h2{\n    color: #fff;\n}\n\n.wrapper2 img{\n    width: 50px;\n    height: 50px;\n    border-radius: 50%;\n    margin-top: 5px;\n}\n\n.wrapper2 .para{\n    margin-left: 10%;\n}\n\n/*========== CONTACT =========*/\n.contact{\n    padding-block-end: calc(var(--section-padding) * 2);\n    text-align: center;\n}\n\n.contact .section-title{\n    font-weight: var(--fw-500);\n}\n\n.contact .section-text{\n    margin-block-end: 25px;\n}\n\n.contact .btn{\n    display: flex;\n    align-items: center;\n    gap: 8px;\n    margin-inline: auto;\n}\n\n.contact .btn ion-icon{\n    --ionicon-stroke-width: 50px\n}\n\n/*=========== FOOTER ===========*/\n\n.footer{\n    background-color: var(--eerie-black);\n    color: var(--light-gray);\n    margin-block-start: 100px;\n    padding-block-start: 190px;\n}\n\n.footer .logo{\n    color: var(--white);\n    font-weight: var(--fw-500);\n    margin-block-end: 25px;\n}\n\n.footer-text{\n    line-height: 1.6;\n}\n\n.footer-top{\n    padding-block: 60px;\n}\n\n.footer-top .container{\n    display: grid;\n    gap: 30px;\n}\n\n.footer-list-title{\n    color: var(--mint-cream);\n    font-weight: var(--fw-600);\n    letter-spacing: 1px;\n    margin-block-end: 25px;\n}\n\n.footer-list li:not(:last-child){\n    margin-block-end: 15px;\n}\n\n.footer-link{\n    display: flex;\n    align-items: center;\n    gap: 5px;\n    transition: var(--transition);\n}\n\n.footer-link ion-icon{\n    font-size: 14px;\n}\n\n.footer-link:is(:hover, :focus){\n    opacity: 0.8px;\n}\n\n.footer-item{\n    display: flex;\n    align-items: center;\n    gap: 10px;\n}\n\n.footer-item:nth-child(2){\n    align-items: flex-start;\n}\n\n.footer-list .footer-item:not(:last-child){\n    margin-block-end: 25px;\n}\n\n.address{\n    line-height: 1.5;\n}\n\n.address-link{\n    color: var(--green-pigment);\n    margin-block-start: 10px;\n    transition: var(--transition);\n}\n\n.address-link:is(:hover, :focus){\n    opacity: 0.8;\n}\n\n.footer-bottom{\n    border-block-start: 1px solid var(--gunmetal);\n    padding-block: 30px;\n    text-align: center;\n}\n\n.copyright{\n    line-height: 1.6;\n    margin-block-end: 30px;\n}\n\n.copyright-link{\n    display: inline-block;\n}\n\n.social-list{\n    display: flex;\n    justify-content: center;\n    align-items: center;\n    gap: 5px;\n}\n\n.social-link{\n    color: var(--manatee);\n    border: 1px solid var(--gunmetal);\n    padding: 4px;\n    border-radius: var(--radius-6);\n}\n\n.social-link:is(:hover, :focus){\n    background-color: var(--green-pigment);\n    border-color: var(--green-pigment);\n    color: var(--white);\n}\n\n/* back to top */\n\n.back-top-btn{\n    position: fixed;\n    bottom: 10px;\n    right: 20px;\n    background-color: var(--green-pigment);\n    color: white;\n    font-size: 14px;\n    padding: 12px;\n    border-radius: 50px;\n    opacity: 0;\n    visibility: hidden;\n    transition: var(--transition);\n    z-index: 4;\n}\n\n.back-top-btn:active{\n    visibility: visible;\n    opacity: 1;\n    transform: translateY(-10px);\n}\n\n\n/*=========== Media Queries */\n\n@media (min-width: 640px){\n    .container{\n        max-width: 640px;\n        width: 100%;\n        margin-inline: auto;\n    }\n\n    .hero .container{\n        max-width: unset;\n    }\n\n    .hero-form-wrapper{\n        max-width: 600px;\n        margin-inline: auto;\n        background-color: unset;\n    }\n\n    .hero .form-tab,\n    .hero-form{\n        background-color: var(--white);\n    }\n\n    .hero .form-tab{\n        max-width: max-content;\n        border-top-left-radius: var(--radius-16);\n        border-top-right-radius: var(--radius-16);\n    }\n\n    .hero-form{\n        border-top-right-radius: var(--radius-16);\n    }\n\n    .footer{\n        padding-block-start: 130px;\n    }\n}\n\n@media (min-width: 768px){\n    :root{\n        --fs-2: 3rem\n    }\n\n    .container,\n    .hero-form-wrapper{\n        max-width: 700px;\n    }\n\n    .section-text{\n        max-width: 55ch;\n        margin-inline: auto;\n    }\n\n    .hero-content{\n        text-align: left;\n        padding-inline: calc(50% - 350px);\n    }\n\n    .hero-title{\n        margin-inline: 0;\n    }\n\n    .hero-text{\n        max-width: 50ch;\n    }\n\n    .hero-form{\n        grid-template-columns: 1fr 1fr;\n    }\n\n    .about .container{\n        display: grid;\n        grid-template-columns: 0.7fr 1fr;\n        align-items: center;\n        gap: 30px;\n    }\n\n    .about-banner{\n        margin-block-end: 0;\n    }\n\n    .service-list,\n    .property-list{\n        grid-template-columns: 1fr 1fr;\n    }\n\n    .footer{\n        padding-block-start: 80px;\n    }\n\n    .footer-top .container{\n        grid-template-columns: repeat(3, 1fr);\n        row-gap: 50px;\n    }\n\n    .footer-brand{\n        grid-column: 1 / 4;\n    }\n\n    .footer-bottom .container{\n        display: flex;\n        justify-content: space-between;\n        align-items: center;\n    }\n\n    .copyright{\n        margin-block-end: 0;\n    }\n}\n\n@media (min-width: 992px){\n    :root{\n        --fs-1: 4.8rem;\n        --section-padding: 40px\n    }\n\n    .container,\n    .hero-form-wrapper{\n        max-width: 900px;\n    }\n\n    .header > .container{\n        gap: 30px;\n    }\n\n    .nav-toggle-btn{\n        display: none;\n    }\n\n    .navbar,\n    .navbar.active{\n        all: unset;\n        margin-inline-start: auto;\n    }\n\n    .navbar-list{\n        margin-block: 0;\n        opacity: 1;\n        display: flex;\n    }\n\n    .navbar-link{\n        padding-inline: 20px;\n    }\n\n    .header .btn{\n        margin-inline-start: 0;\n    }\n\n    .hero-content{\n        padding-inline: calc(50% - 450px);\n    }\n\n    .hero-form{\n        grid-template-columns: repeat(4, 1fr);\n    }\n\n    .about .container{\n        gap: 50px\n    }\n\n    .service-list,\n    .property-list{\n        grid-template-columns: repeat(3, 1fr);\n    }\n\n    .footer-brand{\n        grid-column: auto;\n    }\n\n    .footer-top .container{\n        grid-template-columns: 1fr 0.5fr 0.6fr 0.7fr;\n    }\n}\n\n@media (min-width: 1200px){\n    .container,\n    .hero-form-wrapper{\n        max-width: 1120px;\n    }\n\n    .hero-content{\n        padding-inline: calc(50% - 560px)\n    }\n\n    .about .section-text{\n        max-width: unset;\n    }\n}\n\n@media (max-width: 768px){\n    .wrapper .text{\n        width: 80%;\n    }\n\n    .wrapper .text{\n        height: 600px;\n    }\n}', '\'use strict\';\n\nconst addEventOnElement = function(element, type, listener){\n    if(element.length > 1){\n        for(let i = 0; i < element.length; i++){            \n        element[i].addEventListener(type, listener);\n        }\n    }\n    else{\n        element.addEventListener(type, listener)\n    }\n}\n\n\n//============ NAVBAR ===============//\nconst navbar = document.querySelector(\"[data-navbar]\");\nconst navLinks = document.querySelector(\"[data-nav-link]\");\nconst navToggler = document.querySelector(\"[data-nav-toggler]\");\n\nconst toggleNav = function(){\n    navbar.classList.toggle(\"active\");\n    this.classList.toggle(\"active\");\n}\n\naddEventOnElement(navToggler, \"click\", toggleNav);\n\nconst closeNav = function(){\n    navbar.classList.remove(\"active\");\n    navToggler.classList.remove(\"active\")\n}\n\naddEventOnElement(navLinks, \"clicks\", closeNav);\n\n// Adding the active class to the header\nconst header = document.querySelector(\"[data-header]\");\nconst backTopBtn = document.querySelector(\"[data-back-top-btn]\");\n\nwindow.addEventListener(\"scroll\", function(){\n    if(this.window.scrollY >= 50){\n        header.classList.add(\"active\");\n        backTopBtn.classList.add(\"active\");\n    }else{\n        header.classList.remove(\"active\");\n        backTopBtn.classList.remove(\"active\")\n    }\n});\n\n// Tab Button\nconst tabBtns = document.querySelectorAll(\"[data-tab-btn]\");\n\nlet lastClickedTabBtn = tabBtns[0];\n\nconst changeTab = function(){\n    lastClickedTabBtn.classList.remove(\"active\");\n    this.classList.add(\"active\");\n    lastClickedTabBtn = this;\n}\n\naddEventOnElement(tabBtns, \"click\", changeTab)\n', 'business', 1, NULL, NULL, 1, 3, NULL, NULL, NULL);
